}

export async function open(filename, postscriptName, callback) {
  if (typeof postscriptName === 'function') {
    callback = postscriptName;
    postscriptName = null;
  }

  let buffer = await fs.promises.readFile(filename);
  return create(buffer, postscriptName);
}
